import React, { useState } from "react";
import { quizzes } from "./quizzes";
const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

const gradientColors = [
  "#ff1e1e", // red left
  "#ff6f6f",
  "#aa66ff",
  "#4422aa", // blue right
];

function interpolateColor(value) {
  // value from -20 to 20 scaled 0 to 1
  let t = (value + 20) / 40;
  if (t <= 0.5) {
    // interpolate between red and purple-ish
    const p = t / 0.5;
    return lerpColor("#ff1e1e", "#ffffff", p);
  } else {
    // interpolate between purple-ish and blue
    const p = (t - 0.5) / 0.5;
    return lerpColor("#ffffff", "#4422aa", p);
  }
}

function lerpColor(a, b, t) {
  const c1 = hexToRgb(a);
  const c2 = hexToRgb(b);
  const r = Math.round(c1.r + (c2.r - c1.r) * t);
  const g = Math.round(c1.g + (c2.g - c1.g) * t);
  const b_ = Math.round(c1.b + (c2.b - c1.b) * t);
  return `rgb(${r},${g},${b_})`;
}

function hexToRgb(hex) {
  const m = hex.match(/^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i);
  return m
    ? {
        r: parseInt(m[1], 16),
        g: parseInt(m[2], 16),
        b: parseInt(m[3], 16),
      }
    : null;
}

export default function App() {
  const [languageSelected, setLanguageSelected] = useState(false);
  const [country, setCountry] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [showResult, setShowResult] = useState(false);

  if (!languageSelected) {
    return (
      <div className="start-screen">
        <h1>Political Compass Quiz</h1>
        <p>Choose your language / בחר שפה</p>
        <div className="lang-buttons">
          <button
            onClick={() => {
              setCountry("US");
              setAnswers(Array(quizzes["US"].questions.length).fill(null));
              setLanguageSelected(true);
            }}
            className="lang-btn"
          >
            🇺🇸 English
          </button>
          <button
            onClick={() => {
              setCountry("IL");
              setAnswers(Array(quizzes["IL"].questions.length).fill(null));
              setLanguageSelected(true);
            }}
            className="lang-btn"
          >
            🇮🇱 עברית
          </button>
        </div>
      </div>
    );
  }

  const quiz = quizzes[country];

  const handleAnswer = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  const score = answers.reduce((sum, val) => sum + (val ?? 0), 0);

  const allAnswered = answers.every((a) => a !== null);

  if (showResult && allAnswered) {
    const color = interpolateColor(score);

    return (
      <div className="result-screen">
        <h1>Your Result / התוצאה שלך</h1>
        <p>
          Your Left-Right Score is:{" "}
          <strong style={{ color, fontSize: "2rem" }}>{score.toFixed(1)}</strong>
        </p>
        <div className="score-line">
          <div className="score-line-label left">Far Left</div>
          <div className="score-line-label right">Far Right</div>
          <div
            className="score-marker"
            style={{
              left: `${((score + 20) / 40) * 100}%`,
              backgroundColor: color,
              boxShadow: `0 0 15px 5px ${color}`,
            }}
          />
          <div className="score-gradient" />
        </div>

        <button className="restart-btn" onClick={() => {
          setShowResult(false);
          setAnswers(Array(quiz.questions.length).fill(null));
        }}>
          Retake Quiz / נסה שוב
        </button>
      </div>
    );
  }

  return (
    <div className="quiz-screen">
      <h1>Political Compass Quiz</h1>
      <p>
        {country === "IL" ? "ענה על כל השאלות כדי לראות את התוצאה" : "Answer all questions to see your result"}
      </p>

      <div className="questions-container">
        {quiz.questions.map((q, i) => (
          <div key={i} className="question-block">
            <div className="question-text">{q.q}</div>
            <div className="answers-list">
              {q.options.map((opt, j) => {
                const selected = answers[i] === q.values[j];
                return (
                  <div
                    key={j}
                    className={`answer-item ${selected ? "selected" : ""}`}
                    onClick={() => handleAnswer(i, q.values[j])}
                    role="button"
                    tabIndex={0}
                    onKeyDown={(e) => { if (e.key === 'Enter') handleAnswer(i, q.values[j]) }}
                  >
                    {opt}
                  </div>
                );
              })}
            </div>
          </div>
        ))}
      </div>

      {allAnswered && (
        <button className="submit-btn" onClick={() => setShowResult(true)}>
          {country === "IL" ? "הצג תוצאה" : "Show Result"}
        </button>
      )}
    </div>
  );
}
