import React, { useState } from "react";
import { quizzes } from "./quizzes";

function App() {
  const [country, setCountry] = useState("US");
  const quiz = quizzes[country];
  const [answers, setAnswers] = useState(Array(quiz.questions.length).fill(null));

  const handleAnswer = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  const score = answers.reduce((sum, val) => sum + (val ?? 0), 0);

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        backgroundColor: "#111",
        color: "white",
        padding: "20px",
      }}
    >
      <h1 style={{ marginBottom: "40px" }}>Political Compass Quiz</h1>
      <select
        value={country}
        onChange={(e) => setCountry(e.target.value)}
        style={{
          marginBottom: "40px",
          fontSize: "1.2rem",
          padding: "10px",
          borderRadius: "6px",
          border: "none",
          cursor: "pointer",
        }}
      >
        <option value="US">🇺🇸 USA</option>
        <option value="IL">🇮🇱 ישראל</option>
      </select>

      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          gap: "60px",
          width: "80%",
          maxWidth: "900px",
          flexWrap: "wrap",
        }}
      >
        {quiz.questions.map((q, i) => (
          <div
            key={i}
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              border: "1px solid #444",
              padding: "20px",
              borderRadius: "10px",
              backgroundColor: "#222",
              width: "100%",
              maxWidth: "600px",
              marginBottom: "30px",
              gap: "30px",
              flexWrap: "wrap",
            }}
          >
            <div
              style={{
                flex: "1 1 300px",
                fontSize: "1.2rem",
                textAlign: "center",
              }}
            >
              {q.q}
            </div>
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                gap: "12px",
                flex: "1 1 200px",
              }}
            >
              {q.options.map((opt, j) => {
                const selected = answers[i] === q.values[j];
                return (
                  <div
                    key={j}
                    onClick={() => handleAnswer(i, q.values[j])}
                    style={{
                      cursor: "pointer",
                      padding: "10px 15px",
                      borderRadius: "8px",
                      backgroundColor: selected ? "#4caf50" : "#333",
                      color: selected ? "white" : "#ccc",
                      userSelect: "none",
                      transition: "background-color 0.3s",
                    }}
                  >
                    {opt}
                  </div>
                );
              })}
            </div>
          </div>
        ))}
      </div>

      {answers.every((a) => a !== null) && (
        <div
          style={{
            marginTop: "40px",
            fontSize: "1.4rem",
            backgroundColor: "#222",
            padding: "15px 30px",
            borderRadius: "10px",
            border: "2px solid #4caf50",
          }}
        >
          Your Score: <strong>{score}</strong>
        </div>
      )}
    </div>
  );
}

export default App;
