import React, { useState } from "react";
import { quizzes } from "./quizzes";

function App() {
  const [country, setCountry] = useState("US");
  const quiz = quizzes[country];
  const [answers, setAnswers] = useState(Array(quiz.questions.length).fill(null));

  const handleAnswer = (index, value) => {
    const newAnswers = [...answers];
    newAnswers[index] = value;
    setAnswers(newAnswers);
  };

  const score = answers.reduce((sum, val) => sum + (val ?? 0), 0);

  return (
    <div style={{ padding: "20px" }}>
      <h1>Political Compass Quiz</h1>
      <select value={country} onChange={(e) => setCountry(e.target.value)}>
        <option value="US">üá∫üá∏ USA</option>
        <option value="IL">üáÆüá± ◊ô◊©◊®◊ê◊ú</option>
      </select>
      <div>
        {quiz.questions.map((q, i) => (
          <div key={i} style={{ marginTop: "20px" }}>
            <p>{q.q}</p>
            {q.options.map((opt, j) => (
              <button
                key={j}
                onClick={() => handleAnswer(i, q.values[j])}
                style={{ margin: "5px" }}
              >
                {opt}
              </button>
            ))}
          </div>
        ))}
      </div>
      {answers.every(a => a !== null) && (
        <div style={{ marginTop: "30px" }}>
          <h2>Your Score: {score}</h2>
        </div>
      )}
    </div>
  );
}

export default App;
